INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1983-05-25', 2, 'Star Wars: Episode VI - Return of the Jedi', '2013-04-15 22:40:35.301000', null, null, 2, 'star-wars-episode-vi-return-of-the-jedi', '1', '4', '1', '2013-04-15 22:36:21.682000', null, 4, '0', 'Star Wars Episode VI: Return of the Jedi (also known as Return of the Jedi) is
                    a 1983 American epic space opera film directed by Richard Marquand and written by George Lucas and
                    Lawrence Kasdan, with Lucas as executive producer. It is the third film in the Star Wars franchise
                    and the first film to use THX technology. The film is set approximately one year after The Empire
                    Strikes Back.[2] The evil Galactic Empire, with the help of the villainous Darth Vader, is building
                    a second Death Star in order to crush the Rebel Alliance. Since Emperor Palpatine plans to
                    personally oversee the final stages of its construction, the Rebel Fleet launches a full-scale
                    attack on the Death Star in order to prevent its completion and kill Palpatine, effectively bringing
                    an end to the Empire. Meanwhile, Luke Skywalker, a Rebel leader and Jedi Knight, struggles to bring
                    Vader, who is his father and himself a fallen Jedi, back from the Dark Side of the Force.
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1995-11-19', 2, 'Toy Story', '2013-04-15 22:40:25.322000', null, null, 2, 'toy-story', '1', '5', '1', '2013-04-15 22:36:21.866000', null, 5, '0', 'Toy Story is a 1995 American computer-animated comedy film released by Walt
                    Disney Pictures. It is Pixar''s first feature film as well as the first ever feature film to be made
                    entirely with CGI. The film was directed by John Lasseter and featured the voices of Tom Hanks and
                    Tim Allen.
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1986-01-01', 2, 'Pixar Animation Studios', '2013-04-15 22:42:25.514000', null, null, 3, 'pixar-animation-studios', '1', '6', '1', '2013-04-15 22:36:22.032000', null, 6, '0', 'Pixar was formed in 1986 when Steven Jobs (of Apple and NeXT computer fame)
                    purchased the Lucasfilm Computer Graphics Division from George Lucas. George had decided about a
                    year before that he did not want to continue a hardware development effort in-house. He also did not
                    at that time want to pursue computer-generated animation (as did the employees). He therefore agreed
                    to allow Edwin Catmull, Alvy Ray Smith, and the rest of the employees of the Lucasfilm Computer
                    Division to seek out investors so they could spin off into their own company. Many different options
                    were explored over the course of that year. In the end, the negotiations went down to the very last
                    minute with the outcome not always certain. The deal that was finally made called for $5 million
                    dollars to purchase the division with an additional $5 million for immediate capital investment.
                    Founding members included (in alphabetical order): Tony Apodaca, Loren Carpenter, Ed Catmull, Rob
                    Cook, David DeFrancesco, Tom Duff, Craig Good, Ralph Guggenheim, Pat Hanrahan, Sam Lefler, Darwyn
                    Peachey, Tom Porter, Eben Ostby, Bill Reeves, Alvy Ray Smith, and Rodney Stock.
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1945-03-31', 2, 'Edwin Catmull', '2013-04-15 22:42:26.564000', null, null, 1, 'edwin-catmull', '1', '7', '1', '2013-04-15 22:36:22.220000', null, 7, '0', 'Edwin Earl Catmull, Ph.D. (born 1945) is a computer scientist and current
                    president of Walt Disney Animation Studios and Pixar Animation Studios. As a computer scientist,
                    Catmull has contributed to many important developments in computer graphics.
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1929-01-01', 2, 'Walt Disney Animation Studios', '2013-04-15 22:40:26.072000', null, null, 3, 'walt-disney-animation-studios', '1', '8', '1', '2013-04-15 22:36:22.379000', null, 8, '0', 'Walt Disney Animation Studios is an American animation studio headquartered in
                    Burbank, California. The studio, founded in 1923 as the Disney Brothers Cartoon Studio by brothers
                    Walt and Roy Disney, is the oldest subsidiary of The Walt Disney Company. It is responsible for
                    creating the company''s well-known animated feature films and short subjects.

                    From 1926 to 1929 the studio was known as The Walt Disney Studio. In 1929 the company restructured
                    and diversified under the name Walt Disney Productions, Ltd. The animation studio was then an
                    integrated part of Walt Disney Productions which also included other divisions such as the live
                    action film studio. In 1986 the company again restructured, creating The Walt Disney Company as a
                    separate parent company, and making the animation studio an official subsidiary, called at the time
                    Walt Disney Feature Animation. The division took on its current name in 2007.
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1920-08-03', 2, 'Alexander Schure', '2013-04-15 22:40:26.581000', null, null, 1, 'alexander-schure', '1', '9', '1', '2013-04-15 22:36:22.553000', null, 9, '0', 'Alexander Schure (August 3, 1920 - October 29, 2009)[1] was an American
                    academic. Schure founded the New York Institute of Technology (NYIT) in 1955. He also served as the
                    Chancellor of Nova Southeastern University (NSU) from 1970 until 1985.[2]

                    Schure is credited with saving Nova University, which was in deep financial trouble, after he became
                    the school''s chancellor in 1970.[2] The university is now called Nova Southeastern University, and
                    is now the largest private university in Florida, with more than 28,000 students as of 2009.[2]
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1955-01-01', 2, 'New York Institute of Technology', '2013-04-15 22:40:26.861000', null, null, 3, 'new-york-institute-of-technology', '1', '10', '1', '2013-04-15 22:36:22.726000', null, 10, '0', 'New York Institute of Technology (also known as NYIT) is a private,
                    non-sectarian, co-educational research university in New York City. NYIT has five schools and two
                    colleges, all with a strong emphasis on technology and applied scientific research. The university
                    has two New York campuses, one in Old Westbury Long Island and one near Columbus Circle in
                    Manhattan, as well as several global campuses and programs (e.g. Bahrain, Canada, China, Jordan,
                    United Arab Emirates). NYIT offers a total of 90 undergraduate degree, graduate degree programs, and
                    medical degree programs to 15,000 students in academic areas such as architecture and design; arts
                    and sciences; education; engineering and computing sciences; health professions; management; and
                    osteopathic medicine.
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('2001-11-02', 2, 'Monsters, Inc.', '2013-04-15 22:40:31.856000', null, null, 2, 'monsters-inc', '1', '11', '1', '2013-04-15 22:36:22.875000', null, 11, '0', 'Monsters generate their city''s power by scaring children, but they are
