INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES (null, 2, 'George Lucas', '2013-04-15 22:40:35.054000', null, null, 1, 'george-lucas', '1', '19', '1', '2013-04-15 22:36:23.552000', null, 15, '0', 'George Walton Lucas, Jr. (born May 14, 1944) is an American film producer,
                    screenwriter, director, and entrepreneur. He is the founder, chairman and chief executive of
                    Lucasfilm.[3] He is best known as the creator of the space opera franchise Star Wars and the
                    archaeologist-adventurer character Indiana Jones. Lucas is one of the American film industry''s most
                    financially successful directors/producers, with an estimated net worth of $3.2 billion as of 2011.
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES (null, 2, 'Lucasfilm Limited', '2013-04-15 22:40:29.910000', null, null, 3, 'lucasfilm-limited', '1', '21', '1', '2013-04-15 22:36:23.720000', null, 16, '0', 'Lucasfilm Limited is an American film production company founded by George
                    Lucas in 1971, based in San Francisco, California. Lucas is the company''s current chairman and CEO,
                    and Micheline Chau is the president and COO.
                    The company is best known for producing the Star Wars films, and has also produced other box office
                    hits, including the Indiana Jones franchise and American Graffiti. It has also been a leader in
                    developing new film technology in special effects, sound, and computer animation, and because of
                    their expertise its subsidiaries often help produce non-Lucasfilm pictures. Lucasfilm is set to move
                    away from films and more into TV, due to rising budgets.[2]
                    On July 8, 2005, Lucasfilm''s marketing, online, and licensing units moved into the new Letterman
                    Digital Arts Center located in the Presidio in San Francisco. It shares the complex with Industrial
                    Light & Magic and LucasArts. They are also best known for The Deep Note and THX.
                    Lucasfilm has collaborated with the Walt Disney Company and Walt Disney Imagineering numerous times
                    to create rides and attractions centered on Star Wars and Indiana Jones for various Walt Disney
                    Parks and Resort attractions worldwide.
                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES (null, 2, 'Industrial Light & Magic', '2013-04-15 22:41:22.637000', null, null, 3, 'industrial-light-magic', '1', '22', '1', '2013-04-15 22:36:23.895000', null, 17, '0', 'Industrial Light & Magic (ILM) is an Academy Award-winning motion picture
                    visual effects company that was founded in May 1975 by George Lucas and is owned by Lucasfilm. Lucas
                    created the company when he discovered that the special effects department at 20th Century Fox was
                    shut down after he was given the green light for his production of the film Star Wars. The studio
                    originated in Van Nuys, California, later moved to San Rafael, and is now based at the Letterman
                    Digital Arts Center in the Presidio of San Francisco. Lynwen Brennan who joined the company in 1999,
                    currently serves as the ILM''s President and General Manager.
                ');
