INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1974-01-01', 2, 'New York Institute of Technology Computer Graphics Lab', '2013-04-15 22:40:43.378000', null, null, 3, 'new-york-institute-of-technology-computer-graphics-lab', '1', '25', '1', '2013-04-15 22:36:24.393000', null, 20, '0', 'In 1974 Dr. Alexander Schure, a wealthy entrepreneur, began to assemble the
