INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('2004-01-01', 2, 'The Incredibles', '2013-04-15 22:39:04.988000', null, null, 2, 'the-incredibles', '1', '35', '1', '2013-04-15 22:36:25.821000', null, 28, '0', 'The Incredibles is a 2004 American computer-animated action comedy superhero
                    film about a family of superheroes who are forced to hide their powers. It was written and directed
                    by Brad Bird, a former director and executive consultant of The Simpsons, and was produced by Pixar
                    and distributed by Walt Disney Pictures. The starring voices are Craig T. Nelson as Bob Parr, Holly
                    Hunter as his wife Helen Parr; Sarah Vowell as their teenage daughter Violet; Spencer Fox as their
                    young son Dash; Jason Lee as the supervillain Syndrome; Samuel L. Jackson as Frozone; and Elizabeth
                    PeÃ±a as Syndrome''s beautiful assistant, Mirage. Bob''s yearning to help people draws the entire Parr
                    family into a battle with the villain and his killer robot.
                    The film won the 2004 Annie Award for Best Animated Feature, along with two 2004 Academy Awards,
                    Best Animated Feature and Best Sound Editing. It also received nominations for two other Academy
                    Awards, won the Saturn Award for Best Animated Film, and became the first entirely animated film to
                    win the prestigious Hugo Award for Best Dramatic Presentation. It was also nominated for Best Motion
                    Picture â€“ Musical or Comedy at the 2004 Golden Globes.
