INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1986-01-01', 2, 'Softimage', '2013-04-15 22:41:43.349000', null, null, 3, 'softimage', '1', '225', '1', '2013-04-15 22:36:58.706000', null, 214, '0', 'Formed by Daniel Langlois (one of the three animators on the short film Tony
