INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1970-01-01', 2, 'System Simulation', '2013-04-15 22:41:46.874000', null, null, 3, 'system-simulation', '1', '236', '1', '2013-04-15 22:37:00.436000', 'http://www.ssl.co.uk/', 223, '0', 'John Lansdown founded System Simulation in London with his colleague George
