                ');
INSERT INTO historyofcg_page(date_established, user_id, name, date_modified, date_deceased, image, type_id, vanity_url, user_made, old_id, published, date_created, homepage, id, is_deceased, description) VALUES ('1977-01-01', 2, 'alpha channel', '2013-04-15 22:42:03.782000', null, null, 2, 'alpha-channel', '1', '270', '1', '2013-04-15 22:37:07.147000', null, 257, '0', 'The fourth RGBA information channel that contains 8-bit grayscale values used
