INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:15.756000', 2, '39', 'The name â€œAliasâ€ was arrived at during a brainstorming session when Susan said
