INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:39.534000', 2, '314', null, null, 'Interview with Lee Harrison', null, null, 'SGF0Okaee1o', '1', null, '2013-04-15 22:38:09.745000', 256, 304);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:39.633000', 2, '315', 'Ed Catmull, Tom Porter, Tom Duff, and Alvy Ray Smith received a Scientific and
                    Technical Academy Award in 1996 from the Academy of Motion Picture Arts and Sciences (AMPAS) for
                    "pioneering inventions in digital image compositing" (basically, for the invention of the
                    alpha channel).
                ', null, 'Scientific and Technical Academy Award', null, null, null, '1', '1996-01-01', '2013-04-15 22:38:09.920000', 257, 305);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:39.733000', 2, '316', null, 'http://alvyray.com/Bio/images/AlvyKingForbesASAP4x5.jpg', 'Alvy Ray Smith', null, null, null, '1', null, '2013-04-15 22:38:10.077000', 163, 306);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:39.857000', 2, '317', 'In February 1999, the Academy of Motion Picture Arts and Sciences recognized
                    Discreet Logic''s flame and inferno products for their influence on the advancement of the motion
                    picture industry with a Scientific and Engineering Award.
                ', null, 'Scientific and Engineering Award', null, null, null, '1', '1999-01-01', '2013-04-15 22:38:10.252000', 267, 307);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:39.947000', 2, '318', 'Discreet Logic was acquired by Autodesk in 1999 for about $520 million in stock.
                    Terms of the buyout were modified in early 1999 after an SEC review. The company is located in
                    Montreal.
                ', null, 'The End', null, null, null, '1', '2000-01-01', '2013-04-15 22:38:10.416000', 267, 308);
