INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.154000', 2, '320', null, '
                    http://upload.wikimedia.org/wikipedia/commons/thumb/c/cd/Zbigniew_Rybczy%C5%84ski.jpg/220px-Zbigniew_Rybczy%C5%84ski.jpg
                ', 'Zbigniew Rybczynski', null, null, null, '1', null, '2013-04-15 22:38:10.722000', 289, 310);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.245000', 2, '321', 'Having received his Ph.D from the University of Minnesota, Kenneth Torrance made the
                    move to Cornell University to teach mechanical and aerospace engineering in 1968. During the next 42
                    years, he delighted and challenged students with his kind nature and probing intellect. Torrance
                    used innovative strategies in computer graphics to further his research in heat transfer and fluid
                    mechanics and subsequently became one of ACM SIGGRAPH''s most highly respected researchers. Recipient
                    of the ACM SIGGRAPH Computer Graphics Achievement Award in 1994, Ken Torrance was a familiar face at
                    SIGGRAPH conferences and became a valued volunteer and close-knit member of the SIGGRAPH community.
                    ACM SIGGRAPH mourns the loss of one of our own; he will be dearly missed by all who knew him.
                ', null, 'ACM', null, null, null, '1', null, '2013-04-15 22:38:10.872000', 297, 311);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.353000', 2, '322', null, 'http://cornelldailysun.com/files/images/Pg-1-Kenneth-Torrance-Mug.thumbnail.jpg
                ', 'Kenneth Torrance', null, null, null, '1', null, '2013-04-15 22:38:11.037000', 297, 312);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.461000', 2, '323', null, 'http://www.graphics.cornell.edu/online/box/goral.jpg', 'the original Cornell box', null, null, null, '1', null, '2013-04-15 22:38:11.194000', 299, 313);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.563000', 2, '324', '"Imagination Technologies announces the end of sale and end of life of the
                    SplutterFish Brazil and Rio software plugins for 3ds Max, effective May 14, 2012. No further product
                    development will be done on these products and no purchases will be accepted. There has been no
                    development for Autodesk 3ds Max 2013."', null, 'The End', null, null, null, '1', '2012-05-14', '2013-04-15 22:38:11.376000', 303, 314);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.651000', 2, '325', 'The digital ink and paint system was awarded a Scientific and Technical Achievement
                    Academy Award in 1991. Seven individuals shared the award between Pixar Animation Studios and
                    Disney. Tom Hahn, Peter Nye, and Michael Shantzis of Pixar developed the scan and paint part of the
                    system. Randy Cartwright, Lem Davis, David Coons, Mark Kimball, Jim Houston, and David Wolf of
                    Disney developed the Disney Logistics System (DALS) component.
                ', null, 'Scientific and Technical Achievement Academy Award', null, null, null, '1', '1991-01-01', '2013-04-15 22:38:11.551000', 304, 315);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.744000', 2, '326', 'Unfortunately by 2004 Disney decided that â€œall 3Dâ€ movies where what the public
                    wanted and CAPS (along with the entire 2D division) was shut down.
                ', null, 'The End', null, null, null, '1', '2004-01-01', '2013-04-15 22:38:11.790000', 304, 316);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.833000', 2, '327', 'CAPS was first used on a single scene at the end of The Little Mermaid in 1989. The
                    Rescuers Down Under (1990) was the first film to be produced with no traditional cels at all (making
                    it the first all digital feature film in that respect).
                ', null, 'First CAPS use', null, null, null, '1', '1989-01-01', '2013-04-15 22:38:12', 304, 317);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:40.933000', 2, '328', null, 'http://upload.wikimedia.org/wikipedia/commons/2/25/John_Warnock_2008.jpg', 'John Warnock', null, null, null, '1', null, '2013-04-15 22:38:12.223000', 307, 318);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:41.025000', 2, '329', null, 'http://upload.wikimedia.org/wikipedia/commons/c/c1/Alan_Kay2.jpg', 'Alan Cutis Kay', null, null, null, '1', null, '2013-04-15 22:38:12.429000', 313, 319);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:41.115000', 2, '330', 'In 1993 the developers of Pixar''s RenderMan (Loren Carpenter , Rob Cook, Ed Catmull,
                    Thomas Porter, Pat Hanrahan, Tony Apodaca & Darwyn Peachey) won a Scientific and Engineering
                    Achievement Award from the Academy of Motion Picture Arts and Sciences for RenderMan''s contribution
                    to the motion picture industry.
                ', null, 'Academy Award', null, null, null, '1', '1993-01-01', '2013-04-15 22:38:12.644000', 314, 320);
INSERT INTO historyofcg_story(date_modified, user_id, old_id, text, image, title, source_url, source, video, published, date, date_created, page_id, id) VALUES ('2013-04-15 22:38:41.206000', 2, '331', '"Well, the short version of the story is that back in ''86 or ''87, the hardware
                    people were building VME boards with 16 transputers to run the renderer. Just for grins, one of them
                    (Jeff Mock) built a postcard-size, 4-TP board. Pat was in his office, and they were admiring the
                    coolness of it, thinking how you could put it in a box and render while you''re walking around.
                    Prestoâ€”RenderMan!"  ', null, 'How did Renderman get its name?', null, null, null, '1', '1989-01-01', '2013-04-15 22:38:12.792000', 314, 321);
